import "../css/components/ItemCard.css";
import { Buffer } from "buffer";
import axios from "axios";

const ItemCard = ({ item }) => {
  const dowloadFiles = () => {
    var files = [];
    item.files.forEach((file, i) => {
      axios
        .get(
          `${process.env.REACT_APP_API_URL}/file?accessCode=${file.fileAccessCode}`
        )
        .then((res) => {
          const myBuffer = Buffer.from(res.data);
          const blob = new Blob([myBuffer], { type: file.type });
          console.log(blob);
          const fileName = file.name;
          const link = document.createElement("a");
          if (link.download !== undefined) {
            const url = URL.createObjectURL(blob);
            link.setAttribute("href", url);
            link.setAttribute("download", fileName);
            link.style.visibility = "hidden";
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
          }
        });
    });
  };

  return (
    <div
      className="item-card"
      onClick={() => navigator.clipboard.writeText(item.message)}
    >
      <div>
        <p className="text-large">{item.message}</p>
      </div>

      <div className="item-card-suffix">
        <button onClick={dowloadFiles}>download files</button>
        <p className="text-small">unable after {item.unableAt}</p>
      </div>
    </div>
  );
};

export default ItemCard;
