import "../css/pages/Items.css";
import Navbar from "../components/Navbar";
import ItemCard from "../components/ItemCard";
import axios from "axios";
import { useEffect, useState, useRef } from "react";
import { useSearchParams } from "react-router-dom";
import { toast } from "react-toastify"; // Toast container comes from upper element
import "react-toastify/dist/ReactToastify.css";

const ItemsPage = () => {
  const [items, setItems] = useState([]),
    [files, setFiles] = useState([]),
    [searchParams] = useSearchParams(),
    code = searchParams.get("code"),
    messageRef = useRef(null),
    durationRef = useRef(null),
    [fileSelectedNum, setFileSelectedNum] = useState(0);
  //

  const handleFileChange = (e) => {
    setFiles([]);
    const files = [...e.target.files];
    setFileSelectedNum(files.length);
    const result = [];
    files.forEach(async (file) => {
      const bytes = await convertToBytes(file);
      const data = bytes.split("base64,")[1];
      const type = bytes.split("base64,")[0];
      console.log(data, type);
      result.push({
        name: file.name,
        data: data,
        type: type,
      });
    });
    setFiles(result);
  };

  const convertToBytes = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsDataURL(file);

      fileReader.onload = () => resolve(fileReader.result);

      fileReader.onerror = (error) => reject(error);
    });
  };

  const createNewItem = (event, message, duration, files) => {
    event.preventDefault();
    axios
      .post(`${process.env.REACT_APP_API_URL}/item`, {
        code: code,
        message: message,
        duration: duration,
        files: files,
      })
      .then((res) => {
        getItems(code);
      })
      .catch((error) => {
        console.log(error);
        toast.error(error.response.data.message, {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: false,
          progress: undefined,
          theme: "dark",
        });
      });
  };

  const getItems = (code) => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/item?code=${code}`)
      .then((res) => {
        if (res.status === 200) {
          setItems(res.data.reverse());
        }
      })
      .catch((error) => {
        toast.error(error.response.data.message, {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: false,
          draggable: false,
          progress: undefined,
          theme: "dark",
        });
        setItems([]);
      });
  };

  useEffect(() => {
    getItems(code);
  }, [code]);

  return (
    <div>
      <Navbar />
      <div className="items-page-body page-body">
        <div className="upper-section">
          <div className="title">
            <h3>access code: </h3>
            <h1>{code}</h1>
          </div>
          <div>
            <form
              className="new-item-form"
              onSubmit={(e) =>
                // createNewItem(
                //   e,
                //   messageRef.current.value,
                //   durationRef.current.value,
                //   files
                // )
                console.log(e)
              }
            >
              <div className="new-item-form-elements">
                <input
                  type="text"
                  ref={messageRef}
                  placeholder="message..."
                  required
                  className="new-item-form-input"
                />
                <input
                  type="number"
                  ref={durationRef}
                  minLength="1"
                  min="0"
                  placeholder="duration (min)"
                  required
                  className="new-item-form-input"
                />
                <label for="file" className="input-file-label">
                  {fileSelectedNum > 0
                    ? `${fileSelectedNum} file selected`
                    : "add files"}
                </label>
                <input
                  id="file"
                  type="file"
                  multiple="multiple"
                  className="new-item-form-file"
                  onChange={handleFileChange}
                />
                <input
                  type="submit"
                  value="| create new item"
                  className="new-item-form-submit"
                />
              </div>
            </form>
          </div>
        </div>

        {items.length > 0 ? (
          items.map((item) => <ItemCard key={item.id} item={item} />)
        ) : (
          <div className="empty">
            <h2>There is no items connected to this code!</h2>
          </div>
        )}
      </div>
    </div>
  );
};

export default ItemsPage;
