import "../css/Home.css";
import Navbar from "../components/Navbar";
import { useState, useRef } from "react";

const Home = () => {
  const [open, setIsOpen] = useState(false);
  const codeRef = useRef(null);
  const durationRef = useRef(null);

  const openForm = () => setIsOpen(true);

  function createNewAccessCode(code, duration) {
    e.preventDefault
    console.log(`${codeRef.current.value} ${durationRef.current.value}`);
    return false;
  }

  return (
    <div>
      <Navbar />
      <div className="page-body">
        <button
          style={{ display: `${!open ? "block" : "none"}` }}
          onClick={openForm}
        >
          Create new access code!
        </button>
        <form
          style={{ display: `${open ? "block" : "none"}` }}
          onSubmit={createNewAccessCode}
        >
          <input
            type="text"
            ref={codeRef}
            placeholder="access code..."
            required
          />
          <input
            type="number"
            ref={durationRef}
            minLength="1"
            placeholder="duration"
            required
          />
          <input type="submit" value="create new code" />
        </form>
      </div>
    </div>
  );
};

export default Home;
