import "../css/Home.css";
import Navbar from "../components/Navbar";
import { useState, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();
  const [open, setIsOpen] = useState(false);
  const codeRef = useRef(null);
  const durationRef = useRef(null);

  const openForm = () => setIsOpen(true);

  function createNewAccessCode(code, duration) {
    axios
      .post(`${process.env.REACT_APP_API_URL}/access`, {
        code: code,
        duration: duration,
      })
      .then(navigate(`/items?code=${code}`))
      .catch(function (error) {
        console.log(error);
      });
  }

  return (
    <div>
      <Navbar />
      <div className="page-body">
        <button
          style={{ display: `${!open ? "block" : "none"}` }}
          onClick={openForm}
        >
          Create new access code!
        </button>
        <form
          className="new-code-form"
          style={{ display: `${open ? "block" : "none"}` }}
          onSubmit={() =>
            createNewAccessCode(
              codeRef.current.value,
              durationRef.current.value
            )
          }
        >
          <div className="new-code-form-elements">
            <input
              type="text"
              ref={codeRef}
              placeholder="access code..."
              required
              className="new-code-form-input"
            />
            <input
              type="number"
              ref={durationRef}
              minLength="1"
              placeholder="duration (min)"
              required
              className="new-code-form-input"
            />
            <input
              type="submit"
              value="create new code"
              className="new-code-form-submit"
            />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Home;
