import { useEffect, useRef, useState } from "react";
import "./css/App.css";
import ItemCard from "./components/ItemCard";
import axios from "axios";
const API_URL = "http://localhost:8080/";

const App = () => {
  const [items, setItems] = useState([]);
  const codeRef = useRef(null);
  const getItemsByCode = (code) => {
    axios
      .get(`${API_URL}item?code=${code}`)
      .then((res) => {
        setItems(res.data);
        console.log(items);
      })
      .catch((res) => {
        setItems([]);
        console.log(res.response.data.message);
      });
  };

  return (
    <div className="app">
      <h1>corkboard</h1>
      <div className="search">
        <input ref={codeRef} id="code" placeholder="code..."></input>
      </div>
      <div>
        <button onClick={() => getItemsByCode(codeRef.current.value)}>
          Use Code!
        </button>
        {/* <button>New Code!</button> */}
      </div>
      {items.length > 0 ? (
        items.map((item) => <ItemCard item={item} />)
      ) : (
        <div className="empty">
          <h2>There is no items connected to this code!</h2>
        </div>
      )}
    </div>
  );
};

export default App;
