import "../css/components/Navbar.css";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Navbar = () => {
  const navigate = useNavigate();
  const codeRef = useRef(null);

  const navigateToItemsPage = (event, code) => {
    event.preventDefault();
    axios
      .get(`${process.env.REACT_APP_API_URL}/access?code=${code}`)
      .then((response) => {
        navigate(`/items?code=${code}`);
      })
      .catch((error) => {
        toast.error(error.response.data.message, {
          position: "top-center",
          autoClose: 5000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: false,
          draggable: false,
          progress: undefined,
          theme: "dark",
        });
      });
    return false;
  };

  return (
    <div>
      <div className="flex flex-col md:flex-row justify-between mx-12 mt-4 items-center">
        <a
          href="/"
          className="no-underline text-primary flex items-center text-5xl font-black"
        >
          holdthese
        </a>
        <ToastContainer />
        <form
          className="w-1/2 flex items-center rounded-3xl justify-center p-4 shadow-custom bg-dark-background text-secondary"
          onSubmit={(e) => navigateToItemsPage(e, codeRef.current.value)}
        >
          <input
            required
            className="flex flex-auto font-medium text-xl outline-none font-sans-serif bg-dark-background"
            type="text"
            maxLength="8"
            minLength="1"
            ref={codeRef}
            id="code"
            placeholder="code..."
          />
          <input
            className=" flex font-medium font-sans-serif cursor-pointer outline-none text-xl bg-dark-background"
            type="submit"
            value="search"
          />
        </form>
        <a
          href="/"
          className="no-underline text-primary flex items-center text-5xl font-black"
        >
          sign in
        </a>
      </div>
    </div>
  );
};

export default Navbar;
