import "../css/Items.css";
import Navbar from "../components/Navbar";
import ItemCard from "../components/ItemCard";
import axios from "axios";
import { useEffect, useState, useRef } from "react";
import { useSearchParams } from "react-router-dom";

const ItemsPage = () => {
  const [items, setItems] = useState([]),
    [searchParams] = useSearchParams(),
    code = searchParams.get("code"),
    [open, setIsOpen] = useState(false),
    messageRef = useRef(null),
    durationRef = useRef(null),
    openForm = () => setIsOpen(true);
  //

  const createNewItem = (message, duration) => {
    console.log(message, duration);
  };
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/item?code=${code}`)
      .then((res) => {
        if (res.status === 200) {
          setItems(res.data);
        }
      })
      .catch((res) => {
        console.log(res.response.data.message);
        setItems([]);
      });
  }, [code]);

  return (
    <div>
      <Navbar />
      <div className="items-page-body page-body">
        <div className="upper-section">
          <div className="title">
            <h3>access code: </h3>
            <h1>{code}</h1>
          </div>
          <div>
            <form
              className="new-item-form"
              style={{ display: `${open ? "block" : "none"}` }}
              onSubmit={() =>
                createNewItem(
                  messageRef.current.value,
                  durationRef.current.value
                )
              }
            >
              <div className="new-item-form-elements">
                <input
                  type="text"
                  ref={messageRef}
                  placeholder="message..."
                  required
                  className="new-item-form-input"
                />
                <input
                  type="number"
                  ref={durationRef}
                  minLength="1"
                  placeholder="duration (min)"
                  required
                  className="new-item-form-input"
                />
                <input
                  type="submit"
                  value="create new item"
                  className="new-item-form-submit"
                />
              </div>
            </form>
            <button
              style={{ display: `${!open ? "block" : "none"}` }}
              onClick={openForm}
            >
              create new item
            </button>
          </div>
        </div>

        {items.length > 0 ? (
          items.map((item) => <ItemCard key={item.id} item={item} />)
        ) : (
          <div className="empty">
            <h2>There is no items connected to this code!</h2>
          </div>
        )}
      </div>
    </div>
  );
};

export default ItemsPage;
