import "../css/components/ItemCard.css";
import axios from "axios";

const ItemCard = ({ item }) => {
  const dowloadFiles = () => {
    item.files.forEach((file, i) => {
      axios
        .get(
          `${process.env.REACT_APP_API_URL}/file?accessCode=${file.fileAccessCode}`
        )
        .then(async (res) => {
          const base64Response = await fetch(
            `data:${file.type};base64,${res.data}`
          );
          const newFile = await base64Response.blob();
          const fileName = file.name;
          const link = document.createElement("a");
          if (link.download !== undefined) {
            const url = URL.createObjectURL(newFile);
            link.setAttribute("href", url);
            link.setAttribute("download", fileName);
            link.style.visibility = "hidden";
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
          }
        });
    });
  };

  return (
    <div
      className="cursor-pointer select-text w-4/5 m-1"
      onClick={() => navigator.clipboard.writeText(item.message)}
    >
      <div>
        <p className="text-large">{item.message}</p>
      </div>

      <div className="item-card-suffix">
        {[...item.files].length > 0 ? (
          <button className="dowload-button" onClick={dowloadFiles}>
            download files
          </button>
        ) : (
          <></>
        )}
        <p className="text-small">
          unable after {item.unableAt.substring(11, 16)}{" "}
          {item.unableAt.substring(5, 10)}
        </p>
      </div>
    </div>
  );
};

export default ItemCard;
