import "../css/components/ItemCard.css";

const ItemCard = ({ item }) => {
  const dowloadFiles = () => {
    var files = [...item.files];
    const filesLenght = files.length;
    for (var i = 0; i < filesLenght; i++) {
      const type = files[i].type.split("data:").pop().split(";base64,")[0];
      const data = Buffer.from(files[i].type + files[i].data, "base64");
      var blob = new Blob([data], { type: `${type}` });
      const fileName = `${files[i].name}`;
      const link = document.createElement("a");
      if (link.download !== undefined) {
        const url = URL.createObjectURL(blob);
        link.setAttribute("href", url);
        link.setAttribute("download", fileName);
        link.style.visibility = "hidden";
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
      }
    }
  };

  const b64toBlob = (b64Data, contentType = "", sliceSize = 512) => {
    const byteCharacters = atob(b64Data);
    const byteArrays = [];

    for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
      const slice = byteCharacters.slice(offset, offset + sliceSize);

      const byteNumbers = new Array(slice.length);
      for (let i = 0; i < slice.length; i++) {
        byteNumbers[i] = slice.charCodeAt(i);
      }

      const byteArray = new Uint8Array(byteNumbers);
      byteArrays.push(byteArray);
    }

    const blob = new Blob(byteArrays, { type: contentType });
    return blob;
  };

  return (
    <div
      className="item-card"
      onClick={() => navigator.clipboard.writeText(item.message)}
    >
      <div>
        <p className="text-large">{item.message}</p>
      </div>

      <div className="item-card-suffix">
        <button onClick={dowloadFiles}>download files</button>
        <p className="text-small">unable after {item.unableAt}</p>
      </div>
    </div>
  );
};

export default ItemCard;
