import "../css/components/ItemCard.css";
import { Buffer } from "buffer";
import axios from "axios";

const ItemCard = ({ item }) => {
  const dowloadFiles = () => {
    var files = [];
    item.files.forEach((file, i) => {
      axios
        .get(
          `${process.env.REACT_APP_API_URL}/file?accessCode=${file.fileAccessCode}`
        )
        .then((res) => {
          const newFile = b64toBlob(res.data, file.type);
          var url = URL.createObjectURL(newFile);
          const fileName = file.name;
          const link = document.createElement("a");
          if (link.download !== undefined) {
            const url = URL.createObjectURL(newFile);
            link.setAttribute("href", url);
            link.setAttribute("download", fileName);
            link.style.visibility = "hidden";
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
          }
        });
    });
  };

  function b64toBlob(b64Data, contentType, sliceSize = 512) {
    var byteCharacters = Buffer.from(b64Data, "base64");
    return new File(byteArrays, "pot", { type: contentType });
    // var byteArrays = [];

    // for (var offset = 0; offset < byteCharacters.length; offset += sliceSize) {
      // var slice = byteCharacters.slice(offset, offset + sliceSize);

      // var byteNumbers = new Array(slice.length);
      // for (var i = 0; i < slice.length; i++) {
        // byteNumbers[i] = slice.charCodeAt(i);
      // }

      // var byteArray = new Uint8Array(byteNumbers);

      // byteArrays.push(byteArray);
    // }

    // return new File(byteArrays, "pot", { type: contentType });
  }

  return (
    <div
      className="item-card"
      onClick={() => navigator.clipboard.writeText(item.message)}
    >
      <div>
        <p className="text-large">{item.message}</p>
      </div>

      <div className="item-card-suffix">
        <button onClick={dowloadFiles}>download files</button>
        <p className="text-small">unable after {item.unableAt}</p>
      </div>
    </div>
  );
};

export default ItemCard;
