import { useEffect, useRef, useState } from "react";
import "./App.css";
import ItemCard from "./ItemCard";
import axios from "axios";
const API_URL = "http://localhost:8080/";

const App = () => {
  const [items, setItems] = useState([]);
  const codeRef = useRef(null);
  const getItemsByCode = (code) => {
    axios.get(`${API_URL}item?code=${code}`).then((res) => {
      setItems(res.data);
    });
  };

  const item = {
    message: "blablablaa",
    expiresAt: "2023-27",
  };

  return (
    <div className="app">
      <h1>corkboard</h1>
      <div className="search">
        <input ref={codeRef} id="code" placeholder="code..."></input>
      </div>
      <div>
        <button onClick={() => getItemsByCode(codeRef.current.value)}>
          Use Code!
        </button>
        {/* <button>New Code!</button> */}
      </div>
      <ItemCard item={item} />
    </div>
  );
};

export default App;
