import "./css/Global.css";
import Pages from "./pages/Pages";
import { useRef } from "react";
import "./css/Home.css";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const App = () => {
  const navigate = useNavigate();

  const navigateToItemsPage = (code) => {
    navigate(`/items?code=${code}`);
  };

  const createNewCode = (code) => {
    axios
      .post(`${process.env.REACT_APP_API_URL}/access`, {
        code: code,
        duration: 10000,
      })
      .then((response) => {
        if (response.status === 200) navigateToItemsPage(code);
      });
  };

  const codeRef = useRef(null);

  return (
    <div>
      <div className="navbar">
        <a href="/" className="app-title">
          corkboard
        </a>
        <div className="search">
          <input ref={codeRef} id="code" placeholder="code..."></input>
        </div>
        <div>
          <button onClick={() => navigateToItemsPage(codeRef.current.value)}>
            Use Code!
          </button>
        </div>
        <button onClick={() => createNewCode(codeRef.current.value)}>
          New Code!
        </button>
      </div>
      <Pages />
    </div>
  );
};

export default App;
