import "../css/pages/Home.css";
import Navbar from "../components/Navbar";
import { useState, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate(),
    [open, setIsOpen] = useState(false),
    codeRef = useRef(null),
    durationRef = useRef(null),
    openForm = () => setIsOpen(true);
  //
  async function createNewAccessCode(code, duration) {
    await axios.post(`${process.env.REACT_APP_API_URL}/access`, {
      code: code,
      duration: duration,
    });
    navigate(`/items?code=${code}`);
  }

  return (
    <div>
      <Navbar />
      <div className="page-body">
        <button
          style={{ display: `${!open ? "block" : "none"}` }}
          onClick={openForm}
        >
          create new code
        </button>
        <form
          className="new-code-form"
          style={{ display: `${open ? "block" : "none"}` }}
          onSubmit="return 
            createNewAccessCode(
              codeRef.current.value,
              durationRef.current.value
            )"
        >
          <div className="new-code-form-elements">
            <input
              type="text"
              ref={codeRef}
              placeholder="access code..."
              required
              className="new-code-form-input"
            />
            <input
              type="number"
              ref={durationRef}
              minLength="1"
              placeholder="duration (min)"
              required
              className="new-code-form-input"
            />
            <input
              type="submit"
              value="create new code"
              className="new-code-form-submit"
            />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Home;
